//ChildApi 66florinatoApp
function ChildApi66florinatoApp({ api, profilMap }) {
  const idPersonConnectedFA = localStorage.getItem("idPersonConnectedFA");

  const [checked, setChecked] = useState(false);
  async function Checked() {
    setChecked(!checked);

    const id = api._id; // id
    if(id) { localStorage.setItem("idConversation", id); }

    const idOther = api.idOther;
    if(idOther) { 
      localStorage.setItem("idOther", idOther); 
      localStorage.setItem("idAccountChef", idOther);
    }

    const idGroupOther = api.idGroupOther;
    if(idGroupOther) { localStorage.setItem("idGroupChef", idGroupOther); }


    if (api.idAccount === idPersonConnectedFA) {  //logique pour obtenir l'id de la personne qui va recevoir mon message
      const id = api.idOther;
      const idUser = api.idUserOther;
      localStorage.setItem("idOther", id);      
      localStorage.setItem("idUser", idUser);
    } else {
      const id = api.idAccount;
      const idUser = api.idUser;
      localStorage.setItem("idOther", id);
      localStorage.setItem("idUser", idUser);
      localStorage.setItem("idAccountChef", id);
    }
  }

  // date envoie message
  const dateParser = (date) => {
    let newDate = new Date(date).toLocaleDateString("fr-FR", {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "numeric",
      minute: "numeric",
    });
    return newDate;
  };

  // afficher le groupe
  async function GoTogroupOtherFA() {
    const one = "1";
    const zero = "0";
    localStorage.setItem("GoTogroupOtherFA", one);
    localStorage.setItem("GoTomessageFA", zero);
  }

  // aller vers la page du message
  async function GoTomessageFA() {
    const one = "1";
    const zero = "0";
    localStorage.setItem("GoTomessageFA", one);
    localStorage.setItem("GoTogroupOtherFA", zero);
  }
  
  //afficher les messages uniquement aux personnes concernées (cette logique nous permet d'éviter que les messages que j'ai envoyé ou recu puissent s'afficher a tout le monde)
  const idAccount = api.idAccount === idPersonConnectedFA;
  const idOther = api.idOther === idPersonConnectedFA;
  
  // pour obtenir les informations du profil, (logique pour savoir si c'est l'id de l'expediteur ou du destinataire)
  const idToUse = idAccount ? api.idOther : idOther ? api.idAccount : null;
  const profil = idToUse ? profilMap[idToUse] : null;
  
  const id = api.idAccount === idPersonConnectedFA && api.follow === "1";
  
	/*console.log("idToUse icii ", idToUse);
	console.log("profil icii ", profil);
	console.log("profilMap icii ", profilMap);*/
	
  return (
    <>
    <div className="child" onClick={Checked}>
      {api.type === "30" && idAccount && (<> {/* type=30 , (conversation que j'ai envoyé à Other) */}
      <div className="type30" onClick={GoTomessageFA}>
		{profil ? (<>
        <div className="A"> <img src={profil.photoProfile} alt=""/> </div>
          </>) : (<><p>Profil non trouvé</p> </>)}

        <div className="B">
          <div className="a"> <p>{api.nameOther}</p> </div>
          <div className="b"> <p>{api.lastMessage}</p> </div>
        </div>
        {/* B */}
      </div></>)}
      {/* type=30 , Account */}

      {api.type === "30" && idOther && (<> {/* type=30 , (conversation que Other à reçu) */}
      <div className="type30" onClick={GoTomessageFA}>
		{profil ? (<>
        <div className="A"> <img src={profil.photoProfile} alt=""/> </div>
		</>) : (<><p>Profil non trouvé</p> </>)}

        <div className="B">
          <div className="a"> <p>{api.nameAccount}</p> </div>
          <div className="b"> <p>{api.lastMessage}</p> </div>
        </div>
        {/* B */}
      </div></>)}
      {/* type=30 , Other */}

    
      {api.type === "50" && id && (<> {/* type=50 , je me suis abonné */}
      <div className="type50" onClick={GoTogroupOtherFA}>
		{profil ? (<>
        <div className="A"> <img src={profil.photoProfile} alt=""/> </div>
		</>) : (<><p>Profil non trouvé</p> </>)}

        <div className="B">
          <div className="a"> <pre>{api.nameOther}</pre> </div>
          <div className="a"> <pre>{api.lastMessage}</pre> </div>
          <div className="b"> <p>{dateParser(api.createdAt)}</p> </div>
        </div>
        {/* B */}
      </div></>)} 
      {/* type=50 , je me suis abonné */}
    </div>
    {/* child */}
    </>
  );
} 
