//on affiche les conversations 
function ChildApiConversationAA({ api }) {
  const idPersonConnectedAA = localStorage.getItem("idPersonConnectedAA");

  const [checked, setChecked] = useState(false);
  async function Checked() {
    setChecked(!checked);
    const idConversation = api._id; // id
    localStorage.setItem("idConversationAA", idConversation);

    const idLastMessage = api.idLastMessage; // idLastMessage (id du dernier message)
    if (idLastMessage) { localStorage.setItem("idLastMessageAA", idLastMessage) }
    
    if (api.idOwner === idPersonConnectedAA) {  //logique pour obtenir l'id de la personne qui va recevoir mon message
      const idOwnerAA = api.idOther;
      localStorage.setItem("idOwnerAA", idOwnerAA);

      const idUser = api.idUserOther;
      localStorage.setItem("idUserOwnerAA", idUser);
    } else {
      const idOwner = api.idOwner;
      localStorage.setItem("idOwnerAA", idOwner);

      const idUser = api.idUserOwner;
      localStorage.setItem("idUserOwnerAA", idUser);
    }
  }

  // allons vers la page du message
  async function GoToPageMessage() {
    const pageMessage = "1";
    localStorage.setItem("GoToPageMessageAA", pageMessage);
  }


  //afficher les messages uniquement aux personnes concernées (cette logique nous permet d'éviter que les messages que j'ai envoyé ou recu puissent s'afficher a tout le monde)
  const idOwner = api.idOwner === idPersonConnectedAA;
  const idOther = api.idOther === idPersonConnectedAA;

  return (
    <>
      <div onClick={Checked} className="conversation">
        {idOther && (<>
        <div onClick={GoToPageMessage} className="display">
          <div className="a">
            {api.photoOwner === "0" ? (<><SvgProfil /></>
            ) : (<><img src={api.photoOwner} alt="" /></>)}
          </div>
          {/* a */}

          <div className="b">
            <div className="p1">
              <p>{api.nameOwner}</p> 
              {api.badgeOwner > 0 && (<><SvgBadge /></>)}
            </div>

            <div className="p2">
              {api.lastMessage.length > 65 ? (<><p>{api.lastMessage.slice(0, 65)}...</p></>
              ) : (<><p>{api.lastMessage}</p></>)}
            </div>
          </div>
          {/* b */}
        </div> </>)}

        {idOwner && (<>
        <div onClick={GoToPageMessage} className="display">
          <div className="a">
            {api.photoOther === "0" ? (<><SvgProfil /></>
            ) : (<><img src={api.photoOther} alt="" /></>)}
          </div>
          {/* a */}

          <div className="b">
            <div className="p1">
              <p>{api.nameOther}</p> 
              {api.badgeOther > 0 && (<><SvgBadge /></>)}
            </div>

            <div className="p2">
              {dev && (<>
              <div className="a">
                <div className="aa"><SvgValidate6 /></div>
                <div className="bb"><SvgValidate6 /></div>
              </div> </>)}

              {api.lastMessage.length > 65 ? (<><div className="b"> <p>{api.lastMessage.slice(0, 65)}...</p> </div>
              </>) : (<><div className="b"> <p>{api.lastMessage}</p> </div></>)}
            </div>
          </div>
          {/* b */}

          {dev && (<><div className="c"> <SvgMessage01 /> </div></>)}
         {/* c */}
        </div> </>)}
        {/* display */}
      </div>
    </>
  );
}


