  // logique pour mettre la photo sur son profil Alrani - AA
  const [file, setFile] = useState();
  const [isLoadingPhotoProfileAA, setIsLoadingPhotoProfileAA] = useState(false);

  async function PhotoProfileAA() { 
    if (idPersonConnectedAA) {

      const block = localStorage.getItem("blockAA");
      if (block === "0") { //block on applique ça uniquement aux personnes indesirables qui veulent detruire notre business
      setIsLoadingPhotoProfileAA(true);

      //1ere requete
      //1ere requete
      // on envoie la photo sur cloudinary
      async function premiereReq() {
        const data = new FormData();
        data.append("image", file);
        await axios
        .post(`${process.env.REACT_APP_BACKEND}/api/accountAA/sendPhoto`, data, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        })
        .then((res) => {
          const urlPhoto = res.data.urlPhoto;
          if (urlPhoto) { localStorage.setItem("urlPhotoAAreq", urlPhoto); }

      //2e requete
      //2e requete
      // on enregistre l'url de la photo dans la bdd
      async function deuxiemeReq() {
        const urlPhoto = localStorage.getItem("urlPhotoAAreq");
        await axios({
        method: "put",
        url: `${process.env.REACT_APP_BACKEND}/api/accountAA/update/${idPersonConnectedAA}`,
        data: {
          photoProfile: urlPhoto,
        },
        })
        .then((res) => {
              
      //3e requete
      //3e requete
      //dans cette 3e requete, on fait un appel a l'api pour obtenir les nouvelles donnees
      async function troisiemeReq() {
        await axios
        .get(`${process.env.REACT_APP_BACKEND}/api/accountAA`)
        .then((res) => {
          setApiAccountAA(res.data);
          setPhotoProfileSuccessAA(true);
          setChoosePhotoProfilePageAA(false);
          setIsLoadingPhotoProfileAA(false);

        //8e requete
        //8e requete
        // on obtiens la localisation de l'utilisateur
        async function huitiemeReq() {
          await axios
          .get("https://ip.nf/me.json")
          .then((res) => {
            const getcountryip = res.data.ip.country; //country
            if (getcountryip) { localStorage.setItem("countryIp", getcountryip) }

            const getcityip = res.data.ip.city; //city
            if (getcityip) { localStorage.setItem("cityIp", getcityip) }

            const getuserip = res.data.ip.ip; //ip
            if (getuserip) { localStorage.setItem("addressIp", getuserip) }

        //9e requete
        //9e requete
        // on enregistre la localisation et la navigation de l'utilisateur
        async function neuviemeReq() {
          const id = localStorage.getItem("createIdNavigationAA");
          const addressIp = localStorage.getItem("addressIp");
          const countryIp = localStorage.getItem("countryIp");
          const cityIp = localStorage.getItem("cityIp");
          const page = "Photo de profil remplacée! (il a réussi à mettre sa nouvelle photo de profil) - AA";

          await axios({
            method: "post",
            url: `${process.env.REACT_APP_BACKEND}/api/navigationAA/new`,
            data: {
              id,
              idAccount: idPersonConnectedAA,
              nameAccount: namePersonConnectedAA,
              addressIp,
              countryIp,
              cityIp,
              page,
            },
          })
          .then((res) => {
          const online = res.data.createdAt; //on recupere la date de derniere connexion
          if (online) { localStorage.setItem("onlineAA", online) }

        //10e requete
        //10e requete
        // on enregistre la date de derniere connexion
        async function dixiemeReq() {
          const online = localStorage.getItem("onlineAA");
          await axios({
            method: "put",
            url: `${process.env.REACT_APP_BACKEND}/api/accountAA/update/${idPersonConnectedAA}`,
            data: {
              online,
            },
          });
          }
          dixiemeReq();
          //10e requete
          });
          }
          neuviemeReq();
          //9e requete
          });
          }
          huitiemeReq();
          //8e requete
        })
        .catch((err) => {
          setIsLoadingPhotoProfileAA(false);
          setErrorAA(true);
        });
        }
        troisiemeReq();
        //3e requete
        })
        .catch((err) => {
          setIsLoadingPhotoProfileAA(false);
          setErrorAA(true);
        });
        }
        deuxiemeReq();
        //2e requete
        })
        .catch((err) => {
          setIsLoadingPhotoProfileAA(false);
          setErrorAA(true);
        });
      }
      premiereReq();
      //1ere requete
      }// if (block === "0")
    }// if(idPersonConnectedAA)
  }


