        const idUser = filterUser.map((api) => api._id); // idUser
        if (idUser) { localStorage.setItem("idUser", idUser); }

        const nameUser = filterUser.map((api) => api.nameUser); // nameUser
        if (nameUser) { localStorage.setItem("nameUser", nameUser); }

        const oldBalanceAlraniPay = filterUser.map((api) => api.balanceAlraniPay); // ancien solde Alrani Pay
        if (oldBalanceAlraniPay) { localStorage.setItem("oldBalanceAlraniPay", oldBalanceAlraniPay); }
        
        // calcul du nouveau solde alrani pay
        const newBalanceAlraniPay = parseInt(oldBalanceAlraniPay) + parseInt(writeAmount)
        if (newBalanceAlraniPay) { localStorage.setItem("newBalanceAlraniPay", newBalanceAlraniPay); }

        console.log("idUser", idUser);
        console.log("nameUser", nameUser);
        console.log("virtualNumberPersonConnected", virtualNumberPersonConnected);
