  // logique pour envoyer de l'argent (dans le compte National IA)
  async function TransfertNationalIA() {
    
    // 1ere requete
    // 1ere requete
    // obtenir le solde de l'envoyeur et du beneficiaire
    async function premiereReq() {
      setOpacity(true);
      await axios
      .get(`${process.env.REACT_APP_BACKEND}/api/accountIA`)
      .then((res) => {
        //on filtre en fonction de l'idPersonConnectedIA
        const writeVirtualNumber = localStorage.getItem("writeVirtualNumber");
        const amountToSend = localStorage.getItem("writeAmount");
        const api = res.data;

        // filtre Beneficiaire
        const filterReceiver = api.filter((api) => api.virtualNumber === writeVirtualNumber);
        const idReceiver = filterReceiver.map((api) => api._id); // id receiver
        if (idReceiver) { localStorage.setItem("idReceiver", idReceiver); }

        const nameReceiver = filterReceiver.map((api) => api.nameAccount); // name receiver
        if (nameReceiver) { localStorage.setItem("nameReceiver", nameReceiver); }

        const oldBalanceReceiver = filterReceiver.map((api) => api.balanceNationalAccount); // solde receiver
        if (oldBalanceReceiver) { localStorage.setItem("oldBalanceNationalReceiver", oldBalanceReceiver); }
        
        // verification numero virtuel
        const filterVirtualNumber = api.filter((api) => api.virtualNumber === writeVirtualNumber && api.identification === "1"); //on verifie si le numero virtuel existe et qu'il est deja identifié
        const verificationVirtualNumber = filterVirtualNumber.length > 0;


        // filtre Envoyeur
        const filterSender = api.filter((api) => api._id === idPersonConnectedIA);
        const oldBalanceSender = filterSender.map((api) => api.balanceNationalAccount); // solde sender
        if (oldBalanceSender) { localStorage.setItem("oldBalanceNationalSender", oldBalanceSender); }

        // on fait le calcul
        const fees = parseInt(amountToSend) * 1 / 100; //fees (calcul des frais de transfert)
        if (fees) { localStorage.setItem("fees", fees); }

        const total = parseInt(amountToSend) + parseInt(fees) //total
        if (total) { localStorage.setItem("total", total); }

       

        // calcul des nouveaux soldes
        const newBalanceSender = parseInt(oldBalanceSender) - parseInt(amountToSend) - parseInt(fees); //nouveau solde de l'envoyeur
        if (newBalanceSender) { localStorage.setItem("newBalanceNationalSender", newBalanceSender); }

        const newBalanceReceiver = parseInt(oldBalanceReceiver) + parseInt(amountToSend) //nouveau solde du beneficiaire
        if (newBalanceReceiver) { localStorage.setItem("newBalanceNationalReceiver", newBalanceReceiver); }

        const verifiedBalanceNationalSender = newBalanceSender > 0; //verifiedBalanceNationalSender > 0 alors on transfere l'argent, verifiedBalanceNationalSender < 0 alors on ne transfere pas l'argent
     
        if (verificationVirtualNumber) { //on verifie si le numero virtuel est deja identifier
          // dans ce cas: numero virtuel deja identifier

        if (verifiedBalanceNationalSender) {
          // dans ce cas: solde national suffisant (on debite le solde de l'envoyeur puis on effectue le transfert d'argent)
          
          //2e requete
          //2e requete
          // debiter le solde de l'envoyeur (on debite le solde de l'envoyeur puis on envoie cet argent au beneficiaire)
          async function deuxiemeReq() {
            const newBalanceSender = localStorage.getItem("newBalanceNationalSender");

            await axios({
              method: "put",
              url: `${process.env.REACT_APP_BACKEND}/api/accountIA/update/${idPersonConnectedIA}`,
              data: {
                balanceNationalAccount: newBalanceSender,
              },
            })
            .then((res) => {
                      
          //3e requete
          //3e requete
          // deposer l'argent dans le compte du beneficiaire
          async function troisiemeReq() {
            const idReceiver = localStorage.getItem("idReceiver");
            const newBalanceReceiver = localStorage.getItem("newBalanceNationalReceiver");
                
            await axios({
              method: "put",
              url: `${process.env.REACT_APP_BACKEND}/api/accountIA/update/${idReceiver}`,
              data: {
                balanceNationalAccount: newBalanceReceiver,
              },
            })
            .then((res) => {

          //4e requete
          //4e requete
          // obtenir de nouvelles donnees (on actualise le nouveau solde national de l'envoyeur et on le lui affiche)
          async function quatriemeReq() {
            await axios
              .get(`${process.env.REACT_APP_BACKEND}/api/accountIA`)
              .then((res) => {
                const api = res.data;
                const filterAccount = api.filter((api) => api._id === idPersonConnectedIA);

                const balance = filterAccount.map((api) => api.balanceNationalAccount); //solde national
                if (balance) { localStorage.setItem("balanceNationalAccountIA", balance); }

            //5e requete
            //5e requete
            // on cree une notification (transfert d'argent reussie)
            async function cinquiemeReq() {
              const notification = "4"; // transfert national reussie
              const idOther = localStorage.getItem("idReceiver");
              const nameOther = localStorage.getItem("nameReceiver");
              const amount = localStorage.getItem("writeAmount");
              const fees = localStorage.getItem("fees");
              const total = localStorage.getItem("total");
              const oldBalanceOwner = localStorage.getItem("oldBalanceNationalSender");
              const newBalanceOwner = localStorage.getItem("newBalanceNationalSender");
              const oldBalanceOther = localStorage.getItem("oldBalanceNationalReceiver");
              const newBalanceOther = localStorage.getItem("newBalanceNationalReceiver");

              await axios({
              method: "post",
              url: `${process.env.REACT_APP_BACKEND}/api/notificationIA/create`,
              data: {
                notification,
                amount, // montant envoyé
                fees, // frais de transfert
                total, // total
                idOwner: idPersonConnectedIA,
                nameOwner: namePersonConnectedIA, // nom de l'envoyeur
                oldBalanceOwner, // ancien solde de l'envoyeur
                newBalanceOwner, // nouveau solde de l'envoyeur
                idOther,
                nameOther, // nom du beneficiaire
                oldBalanceOther, // ancien solde du beneficiaire
                newBalanceOther, // nouveau solde du beneficiaire
              },
            }).then((res) => {
              setOpacity(false);
            })
            .catch((err) => {
              setOpacity(false);
              setErrorIA(true);
            });
          }
          cinquiemeReq();
          //5e requete
            })
            .catch((err) => {
              setOpacity(false);
              setErrorIA(true);
            });
          }
          quatriemeReq();
          //4e requete
            })
            .catch((err) => {
              setOpacity(false);
              setErrorIA(true);
            });
          }
          troisiemeReq();
          //3e requete
        })
        .catch((err) => {
          setOpacity(false);
          setErrorIA(true);
        });
      }
      deuxiemeReq();
      // 2e requete
        } else {
          //dans ce cas: solde de l'envoyeur insuffisant
          setOpacity(false);
          setErrorIA(true);
        }
        // le if de verifiedBalanceNationalSender se ferme ici
      } else {
        //dans ce cas: numero virtuel non identifier
        setOpacity(false);
        setErrorIA(true);
        console.log("numero virtuel non identifier ou n'existe pas");
      }
      // le if de verificationVirtualNumber se ferme ici
      })
      .catch((err) => {
        setOpacity(false);
        setErrorIA(true);
      });
    }
    premiereReq();
    // 1ere requete
  }


