  //logique pour se connecter sur Veil Brillant - VB
  async function LoginVB() {
    setOpacity(true);

    //1ere requete
    //1ere requete
    // on verifie si la personne est deja inscrite sur Veil Brillant avec ce numero virtuel
    async function premiereReq() {
      await axios
      .get(`${process.env.REACT_APP_LoginVB}/api/userVB`)
      .then((res) => {
        // on filtre en fonction du numero virtuel saisi
        const writeVirtualNumber = localStorage.getItem("writeVirtualNumberVB");

        const api = res.data;
        const filter = api.filter((api) => api.virtualNumber === writeVirtualNumber);
        
        const verifyAlreadySignup = filter.length > 0; // verifyAlreadySignup > 0 numero virtuel deja inscrit, verifyAlreadySignup < 0 numero virtuel non inscrit
        if (verifyAlreadySignup) {
        // dans ce cas: numero virtuel deja inscrit
      
        //2e requete
        //2e requete
        // connecter l'utilisateur (numero virtuel deja inscrit sur VB alors on lui laisse se connecter)
        async function deuxiemeReq() {
          const writeVirtualNumber = localStorage.getItem("writeVirtualNumberVB");
          const writePassword = localStorage.getItem("writePasswordVB");
          
          await axios({
            method: "post",
            url: `${process.env.REACT_APP_LoginVB}/api/userVB/login`,
            data: {
              virtualNumber: writeVirtualNumber,
              password: writePassword,
            },
          })
          .then((res) => {
            //on prend l'idUser pour pouvoir filtrer
            const idUser = res.data.idUser;
            if (idUser) { localStorage.setItem("idUser", idUser); }
  
          //3e requete
          //3e requete
          // maintenir l'utilisateur connecté ! (on enregistre les infos de connexion)
          async function troisiemeReq() {
          await axios
            .get(`${process.env.REACT_APP_LoginVB}/api/accountVB`)
            .then((res) => {
              //on recupere l'idUser
              const idUser = localStorage.getItem("idUser");
              
              // filtre pour obtenir uniquement les infos de la personne connecter
              const api = res.data;
              const filterUser = api.filter((api) => api.idUser === idUser);
  
              const id = filterUser.map((api) => api._id); // id
              if (id) { localStorage.setItem("idPersonConnectedVB", id); }
              setApiAccountVB(res.data);

            //4e requete
            //4e requete
            // on enregistre displayOther (ca va etre utile lorsqu'il voudra publier)
            async function quatriemeReq() {
            await axios
              .get(`${process.env.REACT_APP_LoginVB}/api/userVB`)
              .then((res) => {
                const idUser = localStorage.getItem("idUser");
                const api = res.data;
                const filterUser = api.filter((api) => api._id === idUser);
    
                const displayOther = filterUser.map((api) => api.displayOther); // displayOther
                if (displayOther) { localStorage.setItem("displayOtherVB", displayOther); }

                const home = filterUser.map((api) => api.home); // home
                if (home) { localStorage.setItem("homeVB", home); }

                const homeVideo = filterUser.map((api) => api.homeVideo); // homeVideo
                if (homeVideo) { localStorage.setItem("homeVideoVB", homeVideo); }

                const block = filterUser.map((api) => api.block); // block
                if (block) { localStorage.setItem("blockVB", block); }
                setApiUserVB(res.data);

                //connexion reussie, on affiche le message de success
                setOpacity(false);
                setConnexionSuccessVB(true);
              })
              }
              quatriemeReq();
              //4e requete
              })
              .catch((err) => {
                setOpacity(false);
                setErreurVB(true);
              });
            }
            troisiemeReq();
            //3e requete
          })
          .catch((err) => {
            setOpacity(false);
            setErreurVB(true);
          });
        }
        deuxiemeReq();
        //2e requete
        } else {
          // numero virtuel non inscrit, impossible de se connecter
          setOpacity(false);
          setErreurVB(true);
        }
        //le if de verifyAlreadySignup se ferme ici
      })
      .catch((err) => {
        setOpacity(false);
        setErreurVB(true);
      });
    }
    premiereReq();
    //1ere requete
  }


