  //logique pour se connecter sur Vixinol - VS
  async function LoginVS() {

    //1ere requete
    //1ere requete
    // on verifie si la personne est deja inscrite sur Vixinol avec ce numero virtuel
    async function premiereReq() {
      setOpacity(true);
      await axios
      .get(`${process.env.REACT_APP_BACKEND}/api/userVS`)
      .then((res) => {
        // on filtre en fonction du numero virtuel saisi
        const writeVirtualNumber = localStorage.getItem("writeVirtualNumber");

        const api = res.data;
        const filter = api.filter((api) => api.virtualNumber === writeVirtualNumber);
        
        const verifyAlreadySignup = filter.length > 0; // verifyAlreadySignup > 0 numero virtuel deja inscrit, verifyAlreadySignup < 0 numero virtuel non inscrit
        if (verifyAlreadySignup) {
        // dans ce cas: numero virtuel deja inscrit
      
        //2e requete
        //2e requete
        // connecter l'utilisateur (numero virtuel deja inscrit sur AA alors on lui laisse se connecter)
        async function deuxiemeReq() {
          const writeVirtualNumber = localStorage.getItem("writeVirtualNumber");
          const writePassword = localStorage.getItem("writePassword");
          
          await axios({
            method: "post",
            url: `${process.env.REACT_APP_BACKEND}/api/userVS/login`,
            data: {
              virtualNumber: writeVirtualNumber,
              password: writePassword,
            },
          })
          .then((res) => {
            //on prend l'idUser pour pouvoir filtrer
            const idUser = res.data.idUser;
            if (idUser) { localStorage.setItem("idUser", idUser); }
  
          //3e requete
          //3e requete
          // maintenir l'utilisateur connecté ! (on enregistre les infos de connexion)
          async function troisiemeReq() {
          await axios
            .get(`${process.env.REACT_APP_BACKEND}/api/accountVS`)
            .then((res) => {
              //on recupere l'idUser
              const idUser = localStorage.getItem("idUser");
              
              // filtre pour obtenir uniquement les infos de la personne connecter
              const api = res.data;
              const filterUser = api.filter((api) => api.idUser === idUser);
  
              const id = filterUser.map((api) => api._id); // id
              if (id) { localStorage.setItem("idPersonConnectedVS", id); }
  
              const name = filterUser.map((api) => api.nameAccount); // name
              if (name) { localStorage.setItem("namePersonConnectedVS", name); }
  
              const virtualNumber = filterUser.map((api) => api.virtualNumber); // virtualNumber
              if (virtualNumber) { localStorage.setItem("virtualNumberPersonConnectedVS", virtualNumber); }

              const phoneNumber = filterUser.map((api) => api.phoneNumber); // phoneNumber
              if (phoneNumber) { localStorage.setItem("phoneNumberPersonConnectedVS", phoneNumber); }

              const photo = filterUser.map((api) => api.photoProfile); // photo
              if (photo) { localStorage.setItem("photoPersonConnectedVS", photo); }
                  
              const paymentMethod = filterUser.map((api) => api.paymentMethod); // paymentMethod
              if (paymentMethod) { localStorage.setItem("paymentMethodVS", paymentMethod); }
  
              const countryCode = filterUser.map((api) => api.countryCode); // countryCode
              if (countryCode) { localStorage.setItem("countryCodeVS", countryCode); }
  
              const typeAccount = filterUser.map((api) => api.type); // typeAccount
              if (typeAccount) { localStorage.setItem("typeAccountVS", typeAccount); }
              
                //connexion reussie: message de succes qui s'affiche et disparait apres quelques secondes
                //ici on affiche le message de succes
                setOpacity(false);
                setConnexionSuccessVS(true);

                //ici on le fait disparaitre
                async function CloseConnexionSuccessVS() {
                  setConnexionSuccessVS(false);
                  setInscriptionPageVS(false);
                  setConnexionPageVS(false);
                  setVixinolStoreApp(true);
                }
                setTimeout(CloseConnexionSuccessVS, 1400);
                console.log("connexion reussie - VS");
              })
              .catch((err) => {
                setOpacity(false);
                setErreurVS(true);
              });
            }
            troisiemeReq();
            //3e requete
          })
          .catch((err) => {
            setOpacity(false);
            setErreurVS(true);
          });
        }
        deuxiemeReq();
        //2e requete
        } else {
          // numero virtuel non inscrit, impossible de se connecter
          setOpacity(false);
          setErreurVS(true);
        }
        //le if de verifyAlreadySignup se ferme ici
      })
      .catch((err) => {
        setOpacity(false);
        setErreurVS(true);
      });
    }
    premiereReq();
    //1ere requete
  }


